import React from 'react';
import { request } from 'graphql-request';
import { gql } from 'apollo-boost';
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from '@apollo/react-hooks';
import { Query } from 'react-apollo'
import './App.css';



function GetMenus(props) {
  return (
    <li>
      {props.Id} ({props.Link})
    </li>
  )
}

function MenuList() {
  const menuQuery = gql`
    {
      menuItems(where: {location: PRIMARY}) {
        nodes {
          menuItemId
          url
          cssClasses
          label
          target
        }
      }
    }
  ` 

  return (
    <Query query={menuQuery}>
        {({ loading, error, data }) => {  
          console.log(data);          // 수정
          if (loading) return <div>Fetching</div>   // 수정
          if (error) return <div>Error</div>        // 수정

          const menuRender = data.menuItems.nodes     // 수정

          return (
            <div>
              {menuRender.map(link => <GetMenus Id={link.menuItemId} Link={link.url} Class={link.cssClasses} Label={link.label} Target={link.target} />)}
            </div>
          )
        }}
      </Query>
  )
}

function App() {
  return <MenuList />
}

export default App;
