import React from 'react';
import { gql } from 'apollo-boost';
import { Query } from 'react-apollo';

function GetMenus(props) {
  let listId = 'menuitem' + props.Id;
  let Classes = 'menuitem ' + props.ClassName;

  return (
    <li key={props.Id} id={listId} className={Classes}>
      <a href={props.Link} target={props.Target}>
        <span>{props.Label}</span>
      </a>
    </li>
  )
}

function Post_Dessert() {
  const dessertQuery = gql`
    {
        gql_desserts {
            nodes {
              title
              featuredImage {
                  altText
                  sourceUrl
                  mediaDetails {
                    width
                    height
                  }
              }
              link
              slug
              gql_dessert_categories {
                  nodes {
                  name
                  }
              }
              gql_dessert_tags {
                  nodes {
                  name
                  }
              }
              contentType {
                  node {
                  name
                  }
              }
              gql_dessertId
              acf_dessert_gal {
                  pdGalImg {
                  altText
                  sourceUrl
                  mediaDetails {
                      width
                      height
                  }
                  }
              }
            }
        }

    }
  `

  return (
    <Query query={dessertQuery}>
      {({ loading, error, data }) => {
        if (loading) return <div>...</div>   // 수정
        if (error) return <div>Error</div>        // 수정

        const menuRender = data.nodes     // 수정
        console.log(menuRender);

        return (
          <nav>
            <ul>
              {menuRender.map(link => <GetMenus key={link.menuItemId} Id={link.menuItemId} Link={link.url} ClassName={link.cssClasses} Label={link.label} Target={link.target} />)}
            </ul>
          </nav>

        )
      }}
    </Query>
  )
}

function Dessert() {
  return <Post_Dessert />
}

export default Dessert;
