import React from 'react';
import { BrowserRouter, Route, Redirect, Link } from 'react-router-dom';
// import Navigation from './components/Navigation';
import Home from './routes/Home';
import Page_Cakes from './routes/Page_Cakes';
import Page_Desserts from './routes/Page_Desserts';
import Page_Macarons from './routes/Page_Macarons';
import { gql } from 'apollo-boost';
import { Query } from 'react-apollo';

function GetMenus(props) {
  let listId = 'menuitem'+props.Id;
  let Classes = 'menuitem '+props.ClassName+' menu_'+props.Slug;

  return (
    <li key={props.Id} id={listId} className={Classes}>
      <Link target={props.Target} to={{
        pathname: `/pages/${props.Slug}`
      }}><span>{props.Label}</span></Link>
    </li>
  )
}

function MenuList() {
  const menuQuery = gql`
    {
      menuItems(where: {location: PRIMARY}) {
        nodes {
          menuItemId
          url
          cssClasses
          label
          target
          title
        }
      }
    }
  ` 

  return (
    <Query query={menuQuery}>
        {({ loading, error, data }) => {  
          if (loading) return <div>...</div>
          if (error) return <div>Error</div>

          const menuRender = data.menuItems.nodes

          return (
              <ul>
                {menuRender.map(link => <GetMenus key={link.menuItemId} Id={link.menuItemId} Link={link.url} ClassName={link.cssClasses} Label={link.label} Target={link.target} Slug={link.title}/>)}
              </ul>
          )
        }}
      </Query>
  )
}

function Navigation() {
  return (
    <nav>
      <MenuList />
    </nav>
  )
}

function Header(){
    return (
        <header className="main_header">
            <BrowserRouter>
                <Link to="/">
                    Logo
                </Link>
                <Navigation />
                <Route path="/" exact={true} component={Home} />
                <Route path="/pages/:props.Slug" exact={true} component={Page_Cakes}/>
                <Route path="/pages/:props.Slug" exact={true} component={Page_Desserts}/>
                <Route path="/pages/:props.Slug" exact={true} component={Page_Macarons}/>
                <Redirect from="*" to="/" />
            </BrowserRouter>
        </header>
    );
}

export default Header;
