import React from 'react';
import { BrowserRouter, Route, Redirect } from 'react-router-dom';
import { gql } from 'apollo-boost';
import Navigation from './components/Navigation';
import Home from './routes/Home';
import Page_Cakes from './routes/Page_Cakes';
import Page_Desserts from './routes/Page_Desserts';
import Page_Macarons from './routes/Page_Macarons';

function Header(){
    const menuQuery = gql`
    {
      menuItems(where: {location: PRIMARY}) {
        nodes {
          menuItemId
          url
          cssClasses
          label
          target
          title
        }
      }
    }
  ` 
    return (
        <header>
            <BrowserRouter>
                <Navigation />
                <Route path="/" exact={true} component={Home} />
                <Route path="/pages/:props.Slug" exact={true} component={Page_Cakes}/>
                <Route path="/pages/:props.Slug" exact={true} component={Page_Desserts}/>
                <Route path="/pages/:props.Slug" exact={true} component={Page_Macarons}/>
                <Redirect from="*" to="/" />
            </BrowserRouter>
        </header>
    );
}

export default Header;
