import React from 'react';
import { request } from 'graphql-request'
import './App.css';

const query = `{
  Movie(title: "Inception") {
    releaseDate
    actors {
      name
    }
  }
}`

request('https://api.graph.cool/simple/v1/movies', query).then(data => console.log(data))

function App() {
  return (
    <div className="App">

    </div>
  );
}

export default App;
